public class PreçoAção {
    private Map<Integer, Double> priceMap; // mapa timestamp -> preço

    public PreçoAção() {
        priceMap = new HashMap<>();
    }

    public void addRecord(int timestamp, double price) {
        priceMap.put(timestamp, price);
    }

    public double getPriceAt(int timestamp) {
        double price = priceMap.get(timestamp);
        if (price == null) {
            // registro não encontrado, retorna null
            return null;
        }
        // caso contrário, retorna o preço atualizado
        return price;
    }

    public double getLatestPrice() {
        return priceMap.values().stream()
                .max((a, b) -> Double.compare(b, a))
                .orElse(null);
    }

    public double getMaxPrice() {
        return priceMap.values().stream()
                .max(Double::max)
                .orElse(null);
    }

    public double getMinPrice() {
        return priceMap.values().stream()
                .min(Double::min)
                .orElse(null);
    }
}
```
