import java.util.Random;

public class Solução {
    private int[] nums;
    private Random random;

    public Solução(int[] nums) {
        this.nums = nums;
        this.random = new Random();
    }

    public int escolher(int target) {
        int count = 0;
        List<Integer> indices = new ArrayList<>();

        for (int i = 0; i < nums.length; i++) {
            if (nums[i] == target) {
                indices.add(i);
                count++;
            }
        }

        return indices.get(random.nextInt(count));
    }
}
