        return latestPrice;
    }

    public double getMaxPrice() {
        return Collections.max(priceMap.values());
    }

    public double getMinPrice() {
        return Collections.min(priceMap.values());
    }

    private void updateLatestPrice() {
        if (priceMap.isEmpty()) {
            latestPrice = Double.NaN;
        } else {
            latestPrice = priceMap.lastEntry().getValue();
        }
    }
}
