public class PrecoAcao {
    private Map<Integer, Double> prices;

    public PrecoAcao() {
        prices = new HashMap<>();
    }

    public void addRecord(int timestamp, double price) {
        prices.put(timestamp, price);
    }

    public double getPriceAt(int timestamp) {
        if (prices.containsKey(timestamp)) {
            return prices.get(timestamp);
        } else {
            // Se o timestamp não for encontrado, retorna o preço mais recente (ou seja, o preço do timestamp mais alto)
            double latestPrice = prices.values().stream().max(Double::compareTo).orElse(0.0);
            return latestPrice;
        }
    }

    public double getLatestPrice() {
        return prices.values().stream().max(Double::compareTo).orElse(0.0);
    }

    public double getMaxPrice() {
        return prices.values().stream().max(Double::compareTo).orElse(0.0);
    }

    public double getMinPrice() {
        return prices.values().stream().min(Double::compareTo).orElse(0.0);
    }

    public void update(int timestamp, double price) {
        if (prices.containsKey(timestamp)) {
            prices.put(timestamp, price);
        } else {
            // Corrigir preço anterior
            double prevPrice = prices.getOrDefault(Math.max(1, timestamp - 1), 0.0);
            prices.put(timestamp, price);
        }
    }

    public double atual() {
        return getLatestPrice();
    }

    public double máximo() {
        return getMaxPrice();
    }

    public double mínimo() {
        return getMinPrice();
    }
}
```

