public static int compressChars(char[] chars) {
    StringBuilder sb = new StringBuilder();
    int count = 1;
    for (int i = 1; i <= chars.length; i++) {
        if (i == chars.length || chars[i] != chars[i - 1]) {
            if (count == 1) {
                sb.append(chars[i - 1]);
            } else {
                sb.append(chars[i - 1]);
                sb.append(count);
            }
            count = 1;
        } else {
            count++;
        }
    }
    // sobreescreve o array original com a compressão
    for (int i = 0; i < sb.length(); i++) {
        chars[i] = sb.charAt(i);
    }
    return sb.length();
}
