public class PreçoAção {
    private Map<Integer, Double> priceMap;

    public PreçoAção() {
        priceMap = new HashMap<>();
    }

    public void addRecord(int timestamp, double price) {
        priceMap.put(timestamp, price);
    }

    public double getPriceAt(int timestamp) {
        Double price = priceMap.get(timestamp);
        if (price == null) {
            // Se não houver registro para esse timestamp, retorne um valor default (por exemplo, 0)
            return 0;
        }
        // Corrigir preço anteriormente errado
        for (int t : priceMap.keySet()) {
            if (t > timestamp) {
                price = priceMap.get(t);
                break;
            }
        }
        return price;
    }

    public double getLatestPrice() {
        return priceMap.isEmpty() ? 0 : priceMap.values().iterator().next();
    }

    public double getMaxPrice() {
        return Collections.max(priceMap.values());
    }

    public double getMinPrice() {
        return Collections.min(priceMap.values());
    }
}
```
