import java.util.HashSet;

public class DoisSomas {
    private HashSet<Integer> numeros;
    private HashSet<Integer> somaCache;

    public DoisSomas() {
        numeros = new HashSet<>();
        somaCache = new HashSet<>();
    }

    public void adicionar(int numero) {
        numeros.add(numero);
        for (int num : numeros) {
            int complemento = valorComplemento(numero, num);
            if (somaCache.contains(complemento)) {
                return; // Encontrou o par de números que soma o valor especificado
            }
        }
        somaCache.clear();
        for (int num : numeros) {
            int complemento = valorComplemento(numero, num);
            somaCache.add(complemento);
        }
    }

    public boolean encontrar(int valor) {
        for (int num : numeros) {
            int complemento = valorComplemento(num, valor - num);
            if (somaCache.contains(complemento)) {
                return true; // Encontrou o par de números que soma o valor especificado
            }
        }
        return false; // Não encontrou o par de números que soma o valor especificado
    }

    private int valorComplemento(int a, int b) {
        return a + b;
    }
}
```
