import java.util.HashMap;
import java.util.Map;

public class PreçoAção {
    private Map<Integer, Double> priceMap; // mapa de timestamp -> preço

    public PreçoAção() {
        priceMap = new HashMap<>();
    }

    public void addRecord(int timestamp, double price) {
        priceMap.put(timestamp, price);
    }

    public double getPriceAt(int timestamp) {
        Double price = priceMap.get(timestamp);
        if (price != null) {
            return price;
        } else {
            // se não houver registro para esse timestamp, retorna o preço mais recente
            double latestPrice = getLatestPrice();
            return latestPrice;
        }
    }

    public double getLatestPrice() {
        if (priceMap.isEmpty()) {
            return Double.NaN; // retorna NaN se não houver registros
        }
        return Collections.max(priceMap.values()); // retorna o preço mais recente
    }

    public double getMaxPrice() {
        if (priceMap.isEmpty()) {
            return Double.NEGATIVE_INFINITY; // retorna infinito se não houver registros
        }
        return Collections.max(priceMap.values()); // retorna o preço máximo
    }

    public double getMinPrice() {
        if (priceMap.isEmpty()) {
            return Double.POSITIVE_INFINITY; // retorna infinito se não houver registros
        }
        return Collections.min(priceMap.values()); // retorna o preço mínimo
    }

    public void update(int timestamp, double price) {
        priceMap.put(timestamp, price);
    }

    public double getCurrentPrice() {
        return getLatestPrice();
    }

    public double getMax() {
        return getMaxPrice();
    }

    public double getMin() {
        return getMinPrice();
    }
}
```
