public int countSolitaryBlackPixels(char[][] picture, int target) {
    int m = picture.length;
    int n = picture[0].length;
    int solitaryPixels = 0;

    // Encontrar linhas de referência com target pixels pretos
    for (int i = 0; i < m; i++) {
        int blackCount = 0;
        for (int j = 0; j < n; j++) {
            if (picture[i][j] == 'B') {
                blackCount++;
            }
        }
        if (blackCount == target) {
            // Linha de referência encontrada
            String referenceRow = "";
            for (int j = 0; j < n; j++) {
                referenceRow += picture[i][j];
            }
        }
    }

    // Verificar colunas com pixels pretos solitários
    for (int j = 0; j < n; j++) {
        boolean isSolitary = true;
        String colRow = "";
        for (int i = 0; i < m; i++) {
            colRow += picture[i][j];
        }
        if (!referenceRow.equals(colRow)) {
            isSolitary = false;
        }
        if (isSolitary) {
            int solitaryPixelCount = 0;
            for (int i = 0; i < m; i++) {
                if (picture[i][j] == 'B' && !referenceRow.equals(String.valueOf(picture[i]))) {
