import axios from 'axios';
import { Weather } from './weather';

const OPENWEATHERMAP_API_KEY = 'YOUR_API_KEY_HERE';
const OPENWEATHERMAP_API_URL = 'http://api.openweathermap.org/data/2.5/weather';

class WeatherService {
  async getWeather(city: string, country: string): Promise<Weather> {
    const params = {
      q: `${city},${country}`,
      units: 'metric', // ou 'imperial' para unidades em Fahrenheit
      appid: OPENWEATHERMAP_API_KEY
    };

    try {
      const response = await axios.get(OPENWEATHERMAP_API_URL, { params });
      const weatherData = response.data.main;
      return new Weather({
        temp: weatherData.temp,
        humidity: weatherData.humidity,
        windSpeed: weatherData.wind.speed
      });
    } catch (error) {
      console.error(error);
      throw new Error('Falha ao recuperar dados do clima');
    }
  }
}

export default WeatherService;
```
