public void rotate(int[] nums, int k) {
    int n = nums.length;
    k = k % n;
    reverse(nums, 0, n - 1); // reversa todo o array
    reverse(nums, 0, k - 1); // reversa a primeira parte do array
    reverse(nums, k, n - 1); // reversa a segunda parte do array
}

private void reverse(int[] nums, int start, int end) {
    while (start < end) {
        int temp = nums[start];
        nums[start] = nums[end];
        nums[end] = temp;
        start++;
        end--;
    }
