    n = len(grid)
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]  # direções para DFS
    ilheus = {}  # dicionário para armazenar áreas de ilhéus

    def dfs(i, j):
        if 0 <= i < n and 0 <= j < n and grid[i][j] == 1:
            grid[i][j] = -1  # marca como visitado
            ilheu_id = len(ilheus)
            ilheus[ilheu_id] = 0
            for di, dj in directions:
                dfs(i + di, j + dj)
            ilheus[ilheu_id] += 1

    for i in range(n):
        for j in range(n):
            if grid[i][j] == 1:
                dfs(i, j)

    max_area = max(ilheus.values())
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 0:
                # encontrar 0 não conectado a nenhum ilhéu
                if all(grid[i + di][j + dj] != 1 for di, dj in directions):
                    grid[i][j] = 1  # alterar 0 para 1
                    break

    # recalcula o tamanho do maior ilhéu
