import java.io.IOException;
import org.apache.commons.csv.CSVFormat;
import org.apache.commons.csv.CSVPrinter;

public class RelatorioPedidos {
    public static void main(String[] args) throws SQLException, IOException {
        // Configuração do banco de dados
        String url = "jdbc:mariadb://localhost:3306/seu_banco_de_dados";
        String usuario = "seu_usuario";
        String senha = "sua_senha";
        String driver = "org.mariadb.jdbc.Driver";

        // Conecta ao banco de dados
        Connection conn = DriverManager.getConnection(url, usuario, senha);

        // Executa a consulta
        Statement stmt = conn.createStatement();
        ResultSet resultSet = stmt.executeQuery("""
            SELECT 
                c.nome AS cliente_nome, 
                c.cidade, 
                p.data_pedido, 
                SUM(p.valor_total) AS valor_total_pedido, 
                GROUP_CONCAT(p.produtos.nome_produto) AS produtos_pedidos
            FROM 
                clientes c 
                INNER JOIN pedidos p ON c.id_cliente = p.id_cliente 
                INNER JOIN produtos pp ON p.id_produto = pp.id_produto 
            WHERE 
                p.data_pedido >= '2022-01-01' 
            GROUP BY 
                c.id_cliente, c.nome, c.cidade
            ORDER BY 
                valor_total_pedido DESC;
        """);

        // Gera o relatório em CSV
        try (CSVPrinter printer = CSVFormat.DEFAULT.withHeader("Cliente", "Cidade", "Data do Pedido", "Valor Total do Pedido", "Produtos Pedidos").print(new FileWriter("relatorio_pedidos.csv"))) {
            while (resultSet.next()) {
                printer.printRecord(
